import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FileText, MessageSquare, PlusCircle, X, Save, ArrowLeft } from 'lucide-react';
import { surveyApi } from '../services/api';
import { SurveyCreate } from '../types';

const CreateSurvey: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<SurveyCreate>({
    title: '',
    description: '',
    options: ['', '']
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validazione
    if (!formData.title.trim()) {
      setError('Il titolo è obbligatorio');
      return;
    }

    const validOptions = formData.options.filter(opt => opt.trim());
    if (validOptions.length < 2) {
      setError('Devi inserire almeno 2 opzioni');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const surveyToCreate = {
        ...formData,
        options: validOptions
      };

      const newSurvey = await surveyApi.createSurvey(surveyToCreate);
      navigate(`/survey/${newSurvey.id}`);
    } catch (err) {
      setError('Errore nella creazione del sondaggio');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const addOption = () => {
    setFormData(prev => ({
      ...prev,
      options: [...prev.options, '']
    }));
  };

  const removeOption = (index: number) => {
    if (formData.options.length > 2) {
      setFormData(prev => ({
        ...prev,
        options: prev.options.filter((_, i) => i !== index)
      }));
    }
  };

  const updateOption = (index: number, value: string) => {
    setFormData(prev => ({
      ...prev,
      options: prev.options.map((opt, i) => i === index ? value : opt)
    }));
  };

  return (
    <div>
      <div style={{ marginBottom: '3rem' }}>
        <h1 style={{ 
          fontSize: '2.5rem', 
          fontWeight: '800', 
          color: 'white',
          textShadow: '0 2px 10px rgba(0, 0, 0, 0.2)',
          marginBottom: '0.5rem'
        }}>
          Crea Nuovo Sondaggio
        </h1>
        <p style={{ 
          color: 'rgba(255, 255, 255, 0.9)', 
          fontSize: '1.125rem',
          textShadow: '0 1px 4px rgba(0, 0, 0, 0.2)'
        }}>
          Raccogli feedback e opinioni con un sondaggio personalizzato
        </p>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="card">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">
              <FileText size={20} />
              Titolo del Sondaggio *
            </label>
            <input
              type="text"
              className="form-input"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              placeholder="Es: Qual è il tuo linguaggio di programmazione preferito?"
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">
              <MessageSquare size={20} />
              Descrizione (opzionale)
            </label>
            <textarea
              className="form-textarea"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Aggiungi una descrizione per fornire più contesto al tuo sondaggio..."
            />
          </div>

          <div className="form-group">
            <label className="form-label">
              <PlusCircle size={20} />
              Opzioni di Risposta * (minimo 2)
            </label>
            {formData.options.map((option, index) => (
              <div key={index} style={{ 
                display: 'flex', 
                marginBottom: '1rem', 
                alignItems: 'center',
                gap: '0.75rem'
              }}>
                <span style={{ 
                  minWidth: '32px',
                  height: '32px',
                  borderRadius: '8px',
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: '600',
                  fontSize: '0.875rem'
                }}>
                  {index + 1}
                </span>
                <input
                  type="text"
                  className="form-input"
                  value={option}
                  onChange={(e) => updateOption(index, e.target.value)}
                  placeholder={`Opzione ${index + 1}`}
                  style={{ flex: 1, margin: 0 }}
                />
                {formData.options.length > 2 && (
                  <button
                    type="button"
                    onClick={() => removeOption(index)}
                    className="btn btn-secondary"
                    style={{ 
                      padding: '0.625rem',
                      minWidth: 'auto',
                      aspectRatio: '1/1'
                    }}
                    title="Rimuovi opzione"
                  >
                    <X size={18} />
                  </button>
                )}
              </div>
            ))}

            <button
              type="button"
              onClick={addOption}
              className="btn btn-secondary"
              style={{ marginTop: '0.75rem', width: '100%' }}
            >
              <PlusCircle size={18} />
              Aggiungi Opzione
            </button>
          </div>

          <div style={{ 
            display: 'flex', 
            gap: '1rem', 
            marginTop: '2.5rem',
            paddingTop: '2rem',
            borderTop: '2px solid rgba(148, 163, 184, 0.1)'
          }}>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
              style={{ flex: 1 }}
            >
              <Save size={18} />
              {loading ? 'Creazione...' : 'Crea Sondaggio'}
            </button>

            <button
              type="button"
              onClick={() => navigate('/')}
              className="btn btn-secondary"
            >
              <ArrowLeft size={18} />
              Annulla
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateSurvey;
