import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { SurveyStats } from '../types';
import { surveyApi } from '../services/api';

const SurveyStatsPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [stats, setStats] = useState<SurveyStats | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (id) {
      loadStats(parseInt(id));
    }
  }, [id]);

  const loadStats = async (surveyId: number) => {
    try {
      setLoading(true);
      const statsData = await surveyApi.getSurveyStats(surveyId);
      setStats(statsData);
      setError(null);
    } catch (err) {
      setError('Errore nel caricamento delle statistiche del sondaggio');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const formatDateTime = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('it-IT', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('it-IT', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
    });
  };

  const getTimeSinceCreation = (dateString: string) => {
    const created = new Date(dateString);
    const now = new Date();
    const diffMs = now.getTime() - created.getTime();
    const diffDays = Math.floor(diffMs / (1000 * 60 * 60 * 24));

    if (diffDays === 0) return 'Oggi';
    if (diffDays === 1) return '1 giorno fa';
    if (diffDays < 7) return `${diffDays} giorni fa`;
    if (diffDays < 30) return `${Math.floor(diffDays / 7)} settimane fa`;
    return `${Math.floor(diffDays / 30)} mesi fa`;
  };

  if (loading) {
    return <div className="loading">Caricamento statistiche...</div>;
  }

  if (error || !stats) {
    return (
      <div className="error">
        {error || 'Statistiche non trovate'}
        <button onClick={() => navigate('/')} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Torna alla Lista
        </button>
      </div>
    );
  }

  return (
    <div>
      <div style={{ marginBottom: '2rem', display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
        <button onClick={() => navigate('/')} className="btn btn-secondary">
          ‚Üê Torna alla Lista
        </button>
        <Link to={`/survey/${stats.survey_id}`} className="btn btn-primary">
          Partecipa al Sondaggio
        </Link>
        <Link to={`/survey/${stats.survey_id}/results`} className="btn btn-secondary">
          Vedi Risultati
        </Link>
      </div>

      <div className="card">
        <h1 className="card-title">üìä Scheda Sondaggio</h1>

        <div style={{ marginBottom: '2rem' }}>
          <h2 style={{ color: '#2c3e50', marginBottom: '0.5rem' }}>{stats.survey_title}</h2>
          {stats.survey_description && (
            <p style={{ color: '#666', fontSize: '1.1rem', lineHeight: '1.5' }}>
              {stats.survey_description}
            </p>
          )}
        </div>

        <div className="stats-grid" style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
          gap: '1.5rem',
          marginBottom: '2rem'
        }}>
          {/* Data di Creazione */}
          <div className="stat-card" style={{
            background: '#e3f2fd',
            padding: '1.5rem',
            borderRadius: '8px',
            border: '2px solid #2196f3'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
              <span style={{ fontSize: '1.5rem', marginRight: '0.5rem' }}>üìÖ</span>
              <h3 style={{ margin: 0, color: '#1976d2' }}>Data Creazione</h3>
            </div>
            <p style={{ margin: 0, fontSize: '1.1rem', fontWeight: 'bold' }}>
              {formatDate(stats.created_at)}
            </p>
            <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
              {getTimeSinceCreation(stats.created_at)}
            </p>
          </div>

          {/* Partecipanti */}
          <div className="stat-card" style={{
            background: '#e8f5e8',
            padding: '1.5rem',
            borderRadius: '8px',
            border: '2px solid #4caf50'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
              <span style={{ fontSize: '1.5rem', marginRight: '0.5rem' }}>üë•</span>
              <h3 style={{ margin: 0, color: '#388e3c' }}>Partecipanti</h3>
            </div>
            <p style={{ margin: 0, fontSize: '2rem', fontWeight: 'bold', color: '#2e7d32' }}>
              {stats.total_participants}
            </p>
            <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
              {stats.total_participants === 1 ? 'persona unica' : 'persone uniche'}
            </p>
          </div>

          {/* Voti Totali */}
          <div className="stat-card" style={{
            background: '#fff3e0',
            padding: '1.5rem',
            borderRadius: '8px',
            border: '2px solid #ff9800'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
              <span style={{ fontSize: '1.5rem', marginRight: '0.5rem' }}>üó≥Ô∏è</span>
              <h3 style={{ margin: 0, color: '#f57c00' }}>Voti Totali</h3>
            </div>
            <p style={{ margin: 0, fontSize: '2rem', fontWeight: 'bold', color: '#ef6c00' }}>
              {stats.total_votes}
            </p>
            <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
              {stats.total_votes === 1 ? 'voto registrato' : 'voti registrati'}
            </p>
          </div>

          {/* Opzioni Disponibili */}
          <div className="stat-card" style={{
            background: '#f3e5f5',
            padding: '1.5rem',
            borderRadius: '8px',
            border: '2px solid #9c27b0'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', marginBottom: '0.5rem' }}>
              <span style={{ fontSize: '1.5rem', marginRight: '0.5rem' }}>üìù</span>
              <h3 style={{ margin: 0, color: '#7b1fa2' }}>Opzioni</h3>
            </div>
            <p style={{ margin: 0, fontSize: '2rem', fontWeight: 'bold', color: '#6a1b9a' }}>
              {stats.options_count}
            </p>
            <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
              {stats.options_count === 1 ? 'opzione disponibile' : 'opzioni disponibili'}
            </p>
          </div>
        </div>

        {/* Ultimo Voto */}
        <div className="card" style={{ background: '#f5f5f5', padding: '1.5rem' }}>
          <div style={{ display: 'flex', alignItems: 'center', marginBottom: '1rem' }}>
            <span style={{ fontSize: '1.5rem', marginRight: '0.5rem' }}>‚è±Ô∏è</span>
            <h3 style={{ margin: 0, color: '#2c3e50' }}>Ultimo Voto</h3>
          </div>
          {stats.last_vote_at ? (
            <div>
              <p style={{ margin: 0, fontSize: '1.1rem', fontWeight: 'bold' }}>
                {formatDateTime(stats.last_vote_at)}
              </p>
              <p style={{ margin: 0, fontSize: '0.9rem', color: '#666' }}>
                {getTimeSinceCreation(stats.last_vote_at)}
              </p>
            </div>
          ) : (
            <p style={{ margin: 0, color: '#666', fontStyle: 'italic' }}>
              Nessun voto ancora registrato
            </p>
          )}
        </div>

        {/* Riepilogo */}
        {stats.total_votes > 0 && (
          <div style={{ marginTop: '2rem', padding: '1rem', background: '#e8f5e8', borderRadius: '8px' }}>
            <h4 style={{ margin: '0 0 0.5rem 0', color: '#2e7d32' }}>üìà Riepilogo Attivit√†</h4>
            <p style={{ margin: 0, color: '#666' }}>
              Questo sondaggio ha ricevuto <strong>{stats.total_votes}</strong> voti da <strong>{stats.total_participants}</strong> partecipanti{' '}
              {stats.total_participants !== stats.total_votes && 
                `(in media ${(stats.total_votes / stats.total_participants).toFixed(1)} voti per partecipante)`
              }.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default SurveyStatsPage;
