import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { surveyApi } from '../services/api';

const Home: React.FC = () => {
  const [qrUrl, setQrUrl] = useState<string>('https://example.com');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadQrUrl();
  }, []);

  const loadQrUrl = async () => {
    try {
      const setting = await surveyApi.getSetting('qr_code_url');
      setQrUrl(setting.value);
    } catch (error) {
      console.error('Errore nel caricamento dell\'URL:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div style={{ textAlign: 'center', padding: '3rem' }}>
        <p>Caricamento...</p>
      </div>
    );
  }

  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      minHeight: '85vh',
      padding: '1rem'
    }}>
      <div style={{
        background: '#ffffff',
        borderRadius: '16px',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.1)',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        gap: '1.5rem',
        padding: '2.5rem',
        maxWidth: '95vw'
      }}>
        <QRCodeSVG 
          value={qrUrl}
          size={Math.min(550, window.innerWidth * 0.65, window.innerHeight * 0.6)}
          level="H"
          includeMargin={true}
        />

        <div style={{
          textAlign: 'center',
          color: '#64748b',
          fontSize: '1rem',
          maxWidth: '600px'
        }}>
          <p style={{ 
            margin: 0, 
            fontWeight: '600', 
            color: '#1e293b',
            wordBreak: 'break-all',
            fontSize: '1.125rem'
          }}>
            {qrUrl}
          </p>
        </div>
      </div>
    </div>
  );
};

export default Home;
