import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Vote, BarChart2, CheckCircle2, Calendar, MessageSquare } from 'lucide-react';
import { Survey } from '../types';
import { surveyApi } from '../services/api';

const SurveyDetail: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [survey, setSurvey] = useState<Survey | null>(null);
  const [selectedOption, setSelectedOption] = useState<number | null>(null);
  const [loading, setLoading] = useState(true);
  const [voting, setVoting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  useEffect(() => {
    if (id) {
      loadSurvey(parseInt(id));
    }
  }, [id]);

  const loadSurvey = async (surveyId: number) => {
    try {
      setLoading(true);
      const surveyData = await surveyApi.getSurvey(surveyId);
      setSurvey(surveyData);
      setError(null);
    } catch (err) {
      setError('Errore nel caricamento del sondaggio');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleVote = async () => {
    if (!selectedOption || !survey) {
      setError('Seleziona un\'opzione per votare');
      return;
    }

    try {
      setVoting(true);
      setError(null);

      await surveyApi.voteSurvey(survey.id, { option_id: selectedOption });
      setSuccess('Voto registrato con successo!');

      setTimeout(() => {
        navigate(`/survey/${survey.id}/results`);
      }, 2000);

    } catch (err: any) {
      if (err.response?.status === 400) {
        setError(err.response.data.detail || 'Errore nella registrazione del voto');
      } else {
        setError('Errore nella registrazione del voto');
      }
      console.error('Errore:', err);
    } finally {
      setVoting(false);
    }
  };

  if (loading) {
    return <div className="loading">Caricamento sondaggio...</div>;
  }

  if (error && !survey) {
    return (
      <div className="error">
        {error}
        <button onClick={() => navigate('/')} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Torna alla Lista
        </button>
      </div>
    );
  }

  if (!survey) {
    return (
      <div className="error">
        Sondaggio non trovato
        <button onClick={() => navigate('/')} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Torna alla Lista
        </button>
      </div>
    );
  }

  return (
    <div>
      <div style={{ marginBottom: '2rem' }}>
        <button onClick={() => navigate('/')} className="btn btn-secondary">
          <ArrowLeft size={18} />
          Torna alla Lista
        </button>
      </div>

      {error && <div className="error">{error}</div>}
      {success && <div className="success">
        <CheckCircle2 size={20} />
        {success}
      </div>}

      <div className="card">
        <div style={{ marginBottom: '2rem' }}>
          <h1 className="card-title">{survey.title}</h1>
          {survey.description && (
            <div style={{
              display: 'flex',
              gap: '0.5rem',
              alignItems: 'flex-start',
              marginTop: '0.75rem',
              padding: '0.875rem',
              background: '#f8fafc',
              borderRadius: '8px',
              border: '1px solid #e2e8f0'
            }}>
              <MessageSquare size={16} style={{ 
                color: '#6366f1', 
                flexShrink: 0,
                marginTop: '0.125rem' 
              }} />
              <p style={{ 
                color: '#475569', 
                margin: 0,
                lineHeight: 1.5,
                fontSize: '0.9375rem',
                fontWeight: '400'
              }}>
                {survey.description}
              </p>
            </div>
          )}

          <div style={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: '0.5rem',
            marginTop: '1rem',
            color: '#64748b',
            fontSize: '0.875rem'
          }}>
            <Calendar size={16} />
            <span>
              Creato il: {new Date(survey.created_at).toLocaleDateString('it-IT', {
                year: 'numeric',
                month: 'long',
                day: 'numeric'
              })}
            </span>
          </div>
        </div>

        <div>
          <h3 style={{ 
            marginBottom: '1rem',
            fontSize: '1.125rem',
            fontWeight: '600',
            color: '#0f172a',
            display: 'flex',
            alignItems: 'center',
            gap: '0.375rem'
          }}>
            <Vote size={18} style={{ color: '#6366f1' }} />
            Seleziona la tua risposta:
          </h3>

          {survey.options.map((option, index) => (
            <div
              key={option.id}
              className={`option-item ${selectedOption === option.id ? 'selected' : ''}`}
              onClick={() => setSelectedOption(option.id)}
              style={{
                animationDelay: `${index * 0.1}s`
              }}
            >
              <input
                type="radio"
                className="option-radio"
                name="survey-option"
                value={option.id}
                checked={selectedOption === option.id}
                onChange={() => setSelectedOption(option.id)}
              />
              <span className="option-text">{option.option_text}</span>
              {selectedOption === option.id && (
                <CheckCircle2 
                  size={24} 
                  style={{ 
                    color: '#667eea',
                    marginLeft: 'auto'
                  }} 
                />
              )}
            </div>
          ))}

          <div style={{ 
            marginTop: '2rem', 
            display: 'flex', 
            gap: '0.5rem',
            paddingTop: '1.5rem',
            borderTop: '1px solid #e2e8f0'
          }}>
            <button
              onClick={handleVote}
              className="btn btn-primary"
              disabled={!selectedOption || voting}
              style={{ flex: 1 }}
            >
              <Vote size={18} />
              {voting ? 'Votando...' : 'Conferma Voto'}
            </button>

            <button
              onClick={() => navigate(`/survey/${survey.id}/results`)}
              className="btn btn-secondary"
            >
              <BarChart2 size={18} />
              Vedi Risultati
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default SurveyDetail;
