import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Survey } from '../types';
import { surveyApi } from '../services/api';

const SurveyList: React.FC = () => {
  const [surveys, setSurveys] = useState<Survey[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  useEffect(() => {
    loadSurveys();
  }, []);

  const loadSurveys = async () => {
    try {
      setLoading(true);
      const surveysData = await surveyApi.getAllSurveys();
      setSurveys(surveysData);
      setError(null);
    } catch (err) {
      setError('Errore nel caricamento dei sondaggi');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteSurvey = async (surveyId: number, surveyTitle: string) => {
    // Conferma eliminazione
    if (!window.confirm(`Sei sicuro di voler eliminare il sondaggio "${surveyTitle}"?\n\nQuesta azione non pu√≤ essere annullata e eliminer√† anche tutti i voti associati.`)) {
      return;
    }

    try {
      await surveyApi.deleteSurvey(surveyId);
      setSuccess(`Sondaggio "${surveyTitle}" eliminato con successo`);
      setError(null);

      // Rimuovi il sondaggio dalla lista locale
      setSurveys(prev => prev.filter(survey => survey.id !== surveyId));

      // Rimuovi il messaggio di successo dopo 3 secondi
      setTimeout(() => setSuccess(null), 3000);
    } catch (err: any) {
      setError(`Errore nell'eliminazione del sondaggio: ${err.response?.data?.detail || 'Errore sconosciuto'}`);
      console.error('Errore eliminazione:', err);
    }
  };

  if (loading) {
    return <div className="loading">Caricamento sondaggi...</div>;
  }

  if (error) {
    return (
      <div className="error">
        {error}
        <button onClick={loadSurveys} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Riprova
        </button>
      </div>
    );
  }

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '2rem' }}>
        <h1>Sondaggi Disponibili</h1>
        <Link to="/create" className="btn btn-primary">
          + Crea Nuovo Sondaggio
        </Link>
      </div>

      {error && <div className="error">{error}</div>}
      {success && <div className="success">{success}</div>}

      {surveys.length === 0 ? (
        <div className="card text-center">
          <h2>Nessun sondaggio disponibile</h2>
          <p>Crea il tuo primo sondaggio per iniziare!</p>
          <Link to="/create" className="btn btn-primary">
            Crea Sondaggio
          </Link>
        </div>
      ) : (
        <div>
          {surveys.map((survey) => (
            <div key={survey.id} className="card">
              <h2 className="card-title">{survey.title}</h2>
              {survey.description && (
                <p className="card-description">{survey.description}</p>
              )}
              <div style={{ marginTop: '1rem' }}>
                <small style={{ color: '#666' }}>
                  Creato il: {new Date(survey.created_at).toLocaleDateString('it-IT')}
                </small>
              </div>
              <div style={{ marginTop: '1rem', display: 'flex', gap: '1rem', flexWrap: 'wrap' }}>
                <Link to={`/survey/${survey.id}`} className="btn btn-primary">
                  Partecipa
                </Link>
                <Link to={`/survey/${survey.id}/results`} className="btn btn-secondary">
                  Vedi Risultati
                </Link>
                <Link to={`/survey/${survey.id}/stats`} className="btn" style={{
                  backgroundColor: '#9c27b0',
                  color: 'white'
                }}>
                  üìä Statistiche
                </Link>
                <button
                  onClick={() => handleDeleteSurvey(survey.id, survey.title)}
                  className="btn"
                  style={{
                    backgroundColor: '#e74c3c',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px'
                  }}
                  onMouseEnter={(e) => {
                    e.currentTarget.style.backgroundColor = '#c0392b';
                  }}
                  onMouseLeave={(e) => {
                    e.currentTarget.style.backgroundColor = '#e74c3c';
                  }}
                >
                  üóëÔ∏è Elimina
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default SurveyList;
