import React, { useState, useEffect } from 'react';
import { QRCodeSVG } from 'qrcode.react';
import { surveyApi } from '../services/api';
import { Home as HomeIcon } from 'lucide-react';
import SidebarLayout from '../components/SidebarLayout';

const Home: React.FC = () => {
  const [qrUrl, setQrUrl] = useState<string>('https://example.com');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadQrUrl();
  }, []);

  const loadQrUrl = async () => {
    try {
      const setting = await surveyApi.getSetting('qr_code_url');
      setQrUrl(setting.value);
    } catch (error) {
      console.error('Errore nel caricamento dell\'URL:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <SidebarLayout title="Home">
        <div style={{ textAlign: 'center', padding: '3rem' }}>
          <p>Caricamento...</p>
        </div>
      </SidebarLayout>
    );
  }

  return (
    <SidebarLayout 
      title="Home"
      icon={<HomeIcon size={28} />}
    >
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        minHeight: '60vh',
        padding: '2rem'
      }}>
        <h2 style={{ marginBottom: '2rem', color: '#1e293b', fontSize: '1.75rem' }}>
          QR Code Survey
        </h2>

        <div style={{
          padding: '2rem',
          background: '#ffffff',
          borderRadius: '12px',
          boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: '1.5rem'
        }}>
          <QRCodeSVG 
            value={qrUrl}
            size={400}
            level="H"
            includeMargin={true}
          />

          <div style={{
            textAlign: 'center',
            color: '#64748b',
            fontSize: '0.875rem'
          }}>
            <p style={{ margin: 0 }}>Scansiona il QR code per accedere a:</p>
            <p style={{ 
              margin: '0.5rem 0 0 0', 
              fontWeight: '600', 
              color: '#3b82f6',
              wordBreak: 'break-all'
            }}>
              {qrUrl}
            </p>
          </div>
        </div>
      </div>
    </SidebarLayout>
  );
};

export default Home;
