// üè¢ Componente Header con loghi partner
import React from 'react';

// Percorsi dei loghi
const LOGOS = {
  surveyApp: '/assets/logos/variants/logo-small.svg',
  databricks: '/assets/logos/partners/databricks-logo.png',
  teamsystem: '/assets/logos/partners/teamsystem-logo.png'
};

interface HeaderLogosProps {
  className?: string;
  style?: React.CSSProperties;
}

const HeaderLogos: React.FC<HeaderLogosProps> = ({ className, style }) => {
  return (
    <div 
      className={`header-logos ${className || ''}`}
      style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        gap: '2rem',
        padding: '1rem 0',
        backgroundColor: '#f8f9fa',
        borderBottom: '1px solid #e9ecef',
        ...style
      }}
    >
      {/* Logo Survey App */}
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <img 
          src={LOGOS.surveyApp} 
          alt="Survey App" 
          style={{ 
            height: '40px',
            filter: 'none' // Mantieni colori originali
          }}
        />
      </div>

      {/* Separatore */}
      <div style={{
        width: '1px',
        height: '30px',
        backgroundColor: '#dee2e6'
      }} />

      {/* Logo Databricks */}
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <img 
          src={LOGOS.databricks} 
          alt="Databricks" 
          style={{ 
            height: '35px',
            objectFit: 'contain'
          }}
        />
      </div>

      {/* Separatore */}
      <div style={{
        width: '1px',
        height: '30px',
        backgroundColor: '#dee2e6'
      }} />

      {/* Logo TeamSystem */}
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <img 
          src={LOGOS.teamsystem} 
          alt="TeamSystem" 
          style={{ 
            height: '35px',
            objectFit: 'contain'
          }}
        />
      </div>
    </div>
  );
};

// Versione compatta per spazi ridotti
export const HeaderLogosCompact: React.FC = () => (
  <div style={{
    display: 'flex',
    alignItems: 'center',
    gap: '1rem',
    padding: '0.5rem 0'
  }}>
    <img src={LOGOS.surveyApp} alt="Survey App" style={{ height: '24px' }} />
    <span style={{ color: '#dee2e6' }}>|</span>
    <img src={LOGOS.databricks} alt="Databricks" style={{ height: '20px' }} />
    <span style={{ color: '#dee2e6' }}>|</span>
    <img src={LOGOS.teamsystem} alt="TeamSystem" style={{ height: '20px' }} />
  </div>
);

// Hook per responsive
export const useHeaderSize = () => {
  const [isCompact, setIsCompact] = React.useState(false);

  React.useEffect(() => {
    const checkSize = () => setIsCompact(window.innerWidth < 768);
    checkSize();
    window.addEventListener('resize', checkSize);
    return () => window.removeEventListener('resize', checkSize);
  }, []);

  return isCompact;
};

// Componente responsive
export const ResponsiveHeaderLogos: React.FC = () => {
  const isCompact = useHeaderSize();

  return isCompact ? <HeaderLogosCompact /> : <HeaderLogos />;
};

export default HeaderLogos;
