import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FileText, MessageSquare, PlusCircle, X, Save, ArrowLeft } from 'lucide-react';
import { surveyApi } from '../services/api';
import { SurveyCreate } from '../types';

const CreateSurvey: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<SurveyCreate>({
    title: '',
    description: '',
    options: ['', '']
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validazione
    if (!formData.title.trim()) {
      setError('Il titolo è obbligatorio');
      return;
    }

    const validOptions = formData.options.filter(opt => opt.trim());
    if (validOptions.length < 2) {
      setError('Devi inserire almeno 2 opzioni');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const surveyToCreate = {
        ...formData,
        options: validOptions
      };

      const newSurvey = await surveyApi.createSurvey(surveyToCreate);
      navigate(`/survey/${newSurvey.id}`);
    } catch (err) {
      setError('Errore nella creazione del sondaggio');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const addOption = () => {
    setFormData(prev => ({
      ...prev,
      options: [...prev.options, '']
    }));
  };

  const removeOption = (index: number) => {
    if (formData.options.length > 2) {
      setFormData(prev => ({
        ...prev,
        options: prev.options.filter((_, i) => i !== index)
      }));
    }
  };

  const updateOption = (index: number, value: string) => {
    setFormData(prev => ({
      ...prev,
      options: prev.options.map((opt, i) => i === index ? value : opt)
    }));
  };

  return (
    <div>
      <h1>Crea Nuovo Sondaggio</h1>

      {error && <div className="error">{error}</div>}

      <div className="card">
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">Titolo del Sondaggio *</label>
            <input
              type="text"
              className="form-input"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              placeholder="Inserisci il titolo del sondaggio..."
              required
            />
          </div>

          <div className="form-group">
            <label className="form-label">Descrizione (opzionale)</label>
            <textarea
              className="form-textarea"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Aggiungi una descrizione per il tuo sondaggio..."
            />
          </div>

          <div className="form-group">
            <label className="form-label">Opzioni di Risposta *</label>
            {formData.options.map((option, index) => (
              <div key={index} style={{ display: 'flex', marginBottom: '0.5rem', alignItems: 'center' }}>
                <input
                  type="text"
                  className="form-input"
                  value={option}
                  onChange={(e) => updateOption(index, e.target.value)}
                  placeholder={`Opzione ${index + 1}`}
                  style={{ marginRight: '0.5rem' }}
                />
                {formData.options.length > 2 && (
                  <button
                    type="button"
                    onClick={() => removeOption(index)}
                    className="btn btn-secondary"
                    style={{ padding: '0.5rem' }}
                  >
                    ✕
                  </button>
                )}
              </div>
            ))}

            <button
              type="button"
              onClick={addOption}
              className="btn btn-secondary"
              style={{ marginTop: '0.5rem' }}
            >
              + Aggiungi Opzione
            </button>
          </div>

          <div style={{ display: 'flex', gap: '1rem', marginTop: '2rem' }}>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
            >
              {loading ? 'Creazione...' : 'Crea Sondaggio'}
            </button>

            <button
              type="button"
              onClick={() => navigate('/')}
              className="btn btn-secondary"
            >
              Annulla
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateSurvey;
