import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Vote, RefreshCw, TrendingUp, Users, Award, MessageSquare, Calendar, User, Star, Heart } from 'lucide-react';
import { SurveyResultsResponse, QuestionType } from '../types';
import { surveyApi } from '../services/api';
import SidebarLayout from '../components/SidebarLayout';

const SurveyResults: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [results, setResults] = useState<SurveyResultsResponse | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (id) {
      loadResults(parseInt(id));
    }
  }, [id]);

  const loadResults = async (surveyId: number) => {
    try {
      setLoading(true);
      const resultsData = await surveyApi.getSurveyResults(surveyId);
      setResults(resultsData);
      setError(null);
    } catch (err) {
      setError('Errore nel caricamento dei risultati');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const getPercentage = (voteCount: number, total: number): number => {
    if (total === 0) return 0;
    return Math.round((voteCount / total) * 100);
  };

  const renderRatingValue = (value: number, icon: string = 'number'): React.ReactNode => {
    const count = Math.floor(value);

    if (icon === 'star') {
      return (
        <span style={{ display: 'flex', alignItems: 'center', gap: '2px' }}>
          {Array.from({ length: count }).map((_, i) => (
            <Star key={i} size={18} fill="#fbbf24" color="#fbbf24" />
          ))}
        </span>
      );
    } else if (icon === 'heart') {
      return (
        <span style={{ display: 'flex', alignItems: 'center', gap: '2px' }}>
          {Array.from({ length: count }).map((_, i) => (
            <Heart key={i} size={18} fill="#ec4899" color="#ec4899" />
          ))}
        </span>
      );
    } else {
      return <span>{value}</span>;
    }
  };

  if (loading) {
    return <div className="loading">Caricamento risultati...</div>;
  }

  if (error || !results) {
    return (
      <div className="error">
        {error || 'Risultati non trovati'}
        <button onClick={() => navigate('/surveys')} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Torna alla Lista
        </button>
      </div>
    );
  }

  const topResult = results.results && results.results.length > 0 
    ? results.results.reduce((prev, current) => 
        (current.vote_count > prev.vote_count) ? current : prev
      )
    : null;

  const isChoiceType = results.question_type === QuestionType.SINGLE_CHOICE || 
                       results.question_type === QuestionType.MULTIPLE_CHOICE;
  const isNumericType = results.question_type === QuestionType.RATING || 
                        results.question_type === QuestionType.SCALE ||
                        results.question_type === QuestionType.NUMBER;

  // Pannello laterale con commenti
  const sidebar = (
    <div>
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '0.5rem',
        marginBottom: '1.5rem',
        paddingBottom: '1rem',
        borderBottom: '2px solid #e2e8f0'
      }}>
        <MessageSquare size={20} style={{ color: '#6366f1' }} />
        <h3 style={{ 
          margin: 0, 
          fontSize: '1.125rem', 
          fontWeight: '600', 
          color: '#0f172a',
          flex: 1
        }}>
          Risposte Aperte
        </h3>
        {results && results.open_responses && results.open_responses.length > 0 && (
          <span style={{
            background: '#6366f1',
            color: 'white',
            borderRadius: '50%',
            width: '24px',
            height: '24px',
            fontSize: '0.75rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: '600'
          }}>
            {results.open_responses.length}
          </span>
        )}
      </div>

      {/* Lista Commenti come Post */}
      {results && results.open_responses && results.open_responses.length > 0 ? (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
          {results.open_responses.map((response, index) => (
            <div 
              key={response.id} 
              style={{
                background: '#f8fafc',
                borderRadius: '8px',
                padding: '1rem',
                border: '1px solid #e2e8f0',
                transition: 'all 0.2s ease'
              }}
            >
              {/* Header del post */}
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.75rem',
                marginBottom: '0.75rem'
              }}>
                {/* Avatar con iniziale */}
                <div style={{
                  width: '36px',
                  height: '36px',
                  borderRadius: '50%',
                  background: `hsl(${(index * 137.5) % 360}, 65%, 55%)`,
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: '600',
                  fontSize: '0.875rem',
                  flexShrink: 0
                }}>
                  <User size={18} />
                </div>

                <div style={{ flex: 1, minWidth: 0 }}>
                  <div style={{ 
                    fontSize: '0.875rem', 
                    fontWeight: '600',
                    color: '#0f172a',
                    marginBottom: '0.125rem'
                  }}>
                    Utente #{index + 1}
                  </div>
                  <div style={{
                    fontSize: '0.75rem',
                    color: '#64748b',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.25rem'
                  }}>
                    <Calendar size={12} />
                    {new Date(response.responded_at).toLocaleDateString('it-IT', {
                      day: 'numeric',
                      month: 'short',
                      year: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </div>
                </div>
              </div>

              {/* Opzione associata (se presente) */}
              {response.option_id && results.results && (
                <div style={{
                  display: 'inline-flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  fontSize: '0.75rem',
                  color: '#6366f1',
                  background: '#ede9fe',
                  padding: '0.25rem 0.625rem',
                  borderRadius: '9999px',
                  fontWeight: '500',
                  marginBottom: '0.75rem'
                }}>
                  <MessageSquare size={12} />
                  {results.results.find(r => r.option_id === response.option_id)?.option_text || `Opzione #${response.option_id}`}
                </div>
              )}

              {/* Contenuto della risposta */}
              <div style={{
                fontSize: '0.875rem',
                color: '#475569',
                lineHeight: 1.6,
                whiteSpace: 'pre-wrap',
                wordBreak: 'break-word'
              }}>
                {response.response_text}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div style={{ 
          textAlign: 'center', 
          padding: '2rem 1rem',
          color: '#94a3b8'
        }}>
          <MessageSquare size={32} style={{ 
            margin: '0 auto 0.75rem',
            opacity: 0.5
          }} />
          <p style={{ 
            fontSize: '0.875rem',
            margin: 0
          }}>
            Nessun commento ancora
          </p>
        </div>
      )}
    </div>
  );

  const mainContent = (
    <div>
      <div style={{ 
        marginBottom: '2rem', 
        display: 'flex', 
        gap: '1rem',
        flexWrap: 'wrap'
      }}>
        <button onClick={() => navigate('/surveys')} className="btn btn-secondary">
          <ArrowLeft size={18} />
          Torna alla Lista
        </button>
        <button onClick={() => navigate(`/survey/${results.survey_id}`)} className="btn btn-primary">
          <Vote size={18} />
          Partecipa al Sondaggio
        </button>
      </div>

      <div className="card">
        <div style={{ marginBottom: '1.5rem' }}>
          <h1 className="card-title">Risultati del Sondaggio</h1>
          <p className="card-description">{results.survey_title}</p>
        </div>

        {/* Stats cards */}
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(180px, 1fr))',
          gap: '0.75rem',
          marginBottom: '2rem'
        }}>
          <div style={{
            padding: '1.25rem',
            borderRadius: '8px',
            background: '#ffffff',
            border: '1px solid #e2e8f0',
            display: 'flex',
            flexDirection: 'column',
            gap: '0.5rem'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.375rem', color: '#6366f1' }}>
              <Users size={16} />
              <span style={{ fontSize: '0.8125rem', fontWeight: '500', textTransform: 'uppercase', letterSpacing: '0.5px' }}>Risposte</span>
            </div>
            <div style={{ fontSize: '1.75rem', fontWeight: '600', color: '#0f172a' }}>
              {results.total_responses}
            </div>
          </div>

          <div style={{
            padding: '1.25rem',
            borderRadius: '8px',
            background: '#ffffff',
            border: '1px solid #e2e8f0',
            display: 'flex',
            flexDirection: 'column',
            gap: '0.5rem'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.375rem', color: '#8b5cf6' }}>
              <TrendingUp size={16} />
              <span style={{ fontSize: '0.8125rem', fontWeight: '500', textTransform: 'uppercase', letterSpacing: '0.5px' }}>Voti Totali</span>
            </div>
            <div style={{ fontSize: '1.75rem', fontWeight: '600', color: '#0f172a' }}>
              {results.total_votes}
            </div>
          </div>

          {topResult && results.total_votes > 0 && isChoiceType && (
            <div style={{
              padding: '1.25rem',
              borderRadius: '8px',
              background: '#ffffff',
              border: '1px solid #e2e8f0',
              display: 'flex',
              flexDirection: 'column',
              gap: '0.5rem',
              gridColumn: results.results.length === 2 ? '1 / -1' : 'auto'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.375rem', color: '#ec4899' }}>
                <Award size={16} />
                <span style={{ fontSize: '0.8125rem', fontWeight: '500', textTransform: 'uppercase', letterSpacing: '0.5px' }}>Pi Votata</span>
              </div>
              <div style={{
                fontSize: '1rem',
                fontWeight: '600',
                color: '#0f172a',
                lineHeight: 1.4
              }}>
                {topResult.option_text}
              </div>
              <div style={{ fontSize: '0.8125rem', color: '#64748b', fontWeight: '400' }}>
                {topResult.vote_count} voti ({getPercentage(topResult.vote_count, results.total_votes)}%)
              </div>
            </div>
          )}

          {isNumericType && results.value_distribution && (
            <div style={{
              padding: '1.25rem',
              borderRadius: '8px',
              background: '#ffffff',
              border: '1px solid #e2e8f0',
              display: 'flex',
              flexDirection: 'column',
              gap: '0.5rem'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.375rem', color: '#10b981' }}>
                <Award size={16} />
                <span style={{ fontSize: '0.8125rem', fontWeight: '500', textTransform: 'uppercase', letterSpacing: '0.5px' }}>Pi Votato</span>
              </div>
              <div style={{ fontSize: '1.75rem', fontWeight: '600', color: '#0f172a', display: 'flex', alignItems: 'center' }}>
                {renderRatingValue(results.value_distribution.reduce((max, item) => item.count > max.count ? item : max, results.value_distribution[0]).value, results.rating_icon || 'number')}
              </div>
              <div style={{ fontSize: '0.8125rem', color: '#64748b', fontWeight: '400' }}>
                {results.value_distribution.reduce((max, item) => item.count > max.count ? item : max, results.value_distribution[0]).count} voti
              </div>
            </div>
          )}

          {isNumericType && !results.value_distribution && results.numeric_stats && (
            <div style={{
              padding: '1.25rem',
              borderRadius: '8px',
              background: '#ffffff',
              border: '1px solid #e2e8f0',
              display: 'flex',
              flexDirection: 'column',
              gap: '0.5rem'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.375rem', color: '#10b981' }}>
                <TrendingUp size={16} />
                <span style={{ fontSize: '0.8125rem', fontWeight: '500', textTransform: 'uppercase', letterSpacing: '0.5px' }}>Media</span>
              </div>
              <div style={{ fontSize: '1.75rem', fontWeight: '600', color: '#0f172a' }}>
                {results.numeric_stats.average.toFixed(2)}
              </div>
            </div>
          )}
        </div>

        {results.total_votes === 0 ? (
          <div className="text-center" style={{ padding: '4rem 2rem' }}>
            <Users size={64} style={{
              margin: '0 auto 2rem',
              color: '#94a3b8',
              opacity: 0.5
            }} />
            <h3 style={{ fontSize: '1.5rem', marginBottom: '1rem' }}>
              Nessun voto ancora registrato
            </h3>
            <p style={{ color: '#64748b', marginBottom: '2rem', fontSize: '1.125rem' }}>
              Sii il primo a votare e a far sentire la tua voce!
            </p>
            <button
              onClick={() => navigate(`/survey/${results.survey_id}`)}
              className="btn btn-primary"
            >
              <Vote size={18} />
              Vota Ora
            </button>
          </div>
        ) : (
          <div>
            {/* Choice Results */}
            {isChoiceType && results.results && results.results.length > 0 && (
              <div>
                <h3 style={{
                  marginBottom: '1.25rem',
                  fontSize: '1.125rem',
                  fontWeight: '600',
                  color: '#0f172a',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.375rem'
                }}>
                  <TrendingUp size={18} style={{ color: '#6366f1' }} />
                  Distribuzione dei Voti:
                </h3>

                {results.results
                  .sort((a, b) => b.vote_count - a.vote_count)
                  .map((result, index) => {
                    const percentage = getPercentage(result.vote_count, results.total_votes);
                    const isTopResult = topResult && result.option_id === topResult.option_id;

                    return (
                      <div
                        key={result.option_id}
                        className="result-item"
                        style={{
                          animationDelay: `${index * 0.1}s`,
                          position: 'relative'
                        }}
                      >
                        {isTopResult && (
                          <div style={{
                            position: 'absolute',
                            top: '-6px',
                            right: '0.875rem',
                            background: '#ec4899',
                            color: 'white',
                            padding: '0.1875rem 0.625rem',
                            borderRadius: '6px',
                            fontSize: '0.6875rem',
                            fontWeight: '600',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.25rem',
                            boxShadow: '0 2px 4px rgba(236, 72, 153, 0.2)'
                          }}>
                            <Award size={12} />
                            Prima
                          </div>
                        )}

                        <div className="result-option">
                          <span style={{ fontSize: '0.9375rem', fontWeight: '500' }}>
                            {result.option_text}
                          </span>
                          <span style={{
                            fontSize: '0.875rem',
                            color: '#6366f1',
                            fontWeight: '600'
                          }}>
                            {result.vote_count} {result.vote_count === 1 ? 'voto' : 'voti'}
                          </span>
                        </div>

                        <div className="result-bar">
                          <div
                            className="result-bar-fill"
                            style={{ width: `${percentage}%` }}
                          />
                          <div className="result-percentage">
                            {percentage}%
                          </div>
                        </div>
                      </div>
                    );
                  })}
              </div>
            )}

            {/* Numeric Results with Options (RATING/SCALE/NUMBER with options) */}
            {isNumericType && results.results && results.results.length > 0 && (
              <div>
                <h3 style={{
                  marginBottom: '1.25rem',
                  fontSize: '1.125rem',
                  fontWeight: '600',
                  color: '#0f172a',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.375rem'
                }}>
                  <TrendingUp size={18} style={{ color: '#6366f1' }} />
                  Risultati per Opzione:
                </h3>

                {results.results
                  .sort((a, b) => (b.numeric_average || 0) - (a.numeric_average || 0))
                  .map((result, index) => (
                    <div
                      key={result.option_id}
                      className="result-item"
                      style={{
                        animationDelay: `${index * 0.1}s`,
                        marginBottom: '1.5rem'
                      }}
                    >
                      <div style={{ marginBottom: '0.75rem' }}>
                        <div style={{ fontSize: '1rem', fontWeight: '600', color: '#0f172a', marginBottom: '0.5rem' }}>
                          {result.option_text}
                        </div>
                        <div style={{
                          display: 'grid',
                          gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
                          gap: '0.75rem',
                          fontSize: '0.875rem'
                        }}>
                          <div>
                            <span style={{ color: '#64748b', fontWeight: '500' }}>Media: </span>
                            <span style={{ color: '#6366f1', fontWeight: '700', fontSize: '1rem' }}>
                              {result.numeric_average?.toFixed(2)}
                            </span>
                          </div>
                          <div>
                            <span style={{ color: '#64748b', fontWeight: '500' }}>Mediana: </span>
                            <span style={{ color: '#8b5cf6', fontWeight: '700', fontSize: '1rem' }}>
                              {result.numeric_median?.toFixed(2)}
                            </span>
                          </div>
                          <div>
                            <span style={{ color: '#64748b', fontWeight: '500' }}>Min: </span>
                            <span style={{ color: '#10b981', fontWeight: '700', fontSize: '1rem' }}>
                              {result.numeric_min}
                            </span>
                          </div>
                          <div>
                            <span style={{ color: '#64748b', fontWeight: '500' }}>Max: </span>
                            <span style={{ color: '#ec4899', fontWeight: '700', fontSize: '1rem' }}>
                              {result.numeric_max}
                            </span>
                          </div>
                          <div>
                            <span style={{ color: '#64748b', fontWeight: '500' }}>Voti: </span>
                            <span style={{ color: '#0f172a', fontWeight: '700', fontSize: '1rem' }}>
                              {result.vote_count}
                            </span>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
              </div>
            )}

            {/* Numeric Stats - Bar Chart for Rating and Scale (senza opzioni) */}
            {isNumericType && !results.results?.length && results.value_distribution && (
              <div>
                <h3 style={{
                  marginBottom: '1.25rem',
                  fontSize: '1.125rem',
                  fontWeight: '600',
                  color: '#0f172a',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.375rem'
                }}>
                  <TrendingUp size={18} style={{ color: '#6366f1' }} />
                  Distribuzione dei Voti:
                </h3>

                {results.value_distribution
                  .sort((a, b) => a.value - b.value)
                  .map((item, index) => {
                    const percentage = getPercentage(item.count, results.total_votes);
                    const maxCount = Math.max(...results.value_distribution!.map(d => d.count));
                    const isHighest = item.count === maxCount && item.count > 0;

                    return (
                      <div
                        key={item.value}
                        className="result-item"
                        style={{
                          animationDelay: `${index * 0.1}s`,
                          position: 'relative'
                        }}
                      >
                        {isHighest && (
                          <div style={{
                            position: 'absolute',
                            top: '-6px',
                            right: '0.875rem',
                            background: '#ec4899',
                            color: 'white',
                            padding: '0.1875rem 0.625rem',
                            borderRadius: '6px',
                            fontSize: '0.6875rem',
                            fontWeight: '600',
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.25rem',
                            boxShadow: '0 2px 4px rgba(236, 72, 153, 0.2)'
                          }}>
                            <Award size={12} />
                            Pi Votato
                          </div>
                        )}

                        <div className="result-option">
                          <span style={{ fontSize: '0.9375rem', fontWeight: '500', display: 'flex', alignItems: 'center' }}>
                            {renderRatingValue(item.value, results.rating_icon || 'number')}
                          </span>
                          <span style={{
                            fontSize: '0.875rem',
                            color: '#6366f1',
                            fontWeight: '600'
                          }}>
                            {item.count} {item.count === 1 ? 'voto' : 'voti'}
                          </span>
                        </div>

                        <div className="result-bar">
                          <div
                            className="result-bar-fill"
                            style={{ width: `${percentage}%` }}
                          />
                          <div className="result-percentage">
                            {percentage}%
                          </div>
                        </div>
                      </div>
                    );
                  })}
              </div>
            )}

            {/* Numeric Stats - Only for NUMBER type */}
            {isNumericType && !results.value_distribution && results.numeric_stats && (
              <div>
                <h3 style={{
                  marginBottom: '1.25rem',
                  fontSize: '1.125rem',
                  fontWeight: '600',
                  color: '#0f172a',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.375rem'
                }}>
                  <TrendingUp size={18} style={{ color: '#6366f1' }} />
                  Statistiche Numeriche:
                </h3>

                <div style={{
                  display: 'grid',
                  gridTemplateColumns: 'repeat(auto-fit, minmax(120px, 1fr))',
                  gap: '1rem',
                  padding: '1.5rem',
                  background: '#f8fafc',
                  borderRadius: '8px',
                  border: '1px solid #e2e8f0'
                }}>
                  <div style={{ textAlign: 'center' }}>
                    <div style={{ fontSize: '0.75rem', color: '#64748b', marginBottom: '0.5rem', textTransform: 'uppercase', fontWeight: '600' }}>
                      Media
                    </div>
                    <div style={{ fontSize: '1.75rem', fontWeight: '700', color: '#6366f1' }}>
                      {results.numeric_stats.average.toFixed(2)}
                    </div>
                  </div>
                  <div style={{ textAlign: 'center' }}>
                    <div style={{ fontSize: '0.75rem', color: '#64748b', marginBottom: '0.5rem', textTransform: 'uppercase', fontWeight: '600' }}>
                      Mediana
                    </div>
                    <div style={{ fontSize: '1.75rem', fontWeight: '700', color: '#8b5cf6' }}>
                      {results.numeric_stats.median.toFixed(2)}
                    </div>
                  </div>
                  <div style={{ textAlign: 'center' }}>
                    <div style={{ fontSize: '0.75rem', color: '#64748b', marginBottom: '0.5rem', textTransform: 'uppercase', fontWeight: '600' }}>
                      Min
                    </div>
                    <div style={{ fontSize: '1.75rem', fontWeight: '700', color: '#10b981' }}>
                      {results.numeric_stats.min_value}
                    </div>
                  </div>
                  <div style={{ textAlign: 'center' }}>
                    <div style={{ fontSize: '0.75rem', color: '#64748b', marginBottom: '0.5rem', textTransform: 'uppercase', fontWeight: '600' }}>
                      Max
                    </div>
                    <div style={{ fontSize: '1.75rem', fontWeight: '700', color: '#ec4899' }}>
                      {results.numeric_stats.max_value}
                    </div>
                  </div>
                </div>
              </div>
            )}


            {/* Date Results */}
            {results.question_type === QuestionType.DATE && results.most_common_date && (
              <div style={{ marginTop: '2rem' }}>
                <h3 style={{
                  marginBottom: '1.25rem',
                  fontSize: '1.125rem',
                  fontWeight: '600',
                  color: '#0f172a',
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.375rem'
                }}>
                  <Calendar size={18} style={{ color: '#6366f1' }} />
                  Data Pi Comune:
                </h3>

                <div style={{
                  padding: '1.5rem',
                  background: '#f8fafc',
                  borderRadius: '8px',
                  border: '1px solid #e2e8f0',
                  textAlign: 'center'
                }}>
                  <div style={{ fontSize: '1.5rem', fontWeight: '600', color: '#6366f1' }}>
                    {new Date(results.most_common_date).toLocaleDateString('it-IT', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric'
                    })}
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        <div style={{
          marginTop: '2rem',
          paddingTop: '1.5rem',
          borderTop: '1px solid #e2e8f0',
          display: 'flex',
          justifyContent: 'center'
        }}>
          <button
            onClick={() => loadResults(results.survey_id)}
            className="btn btn-secondary"
          >
            <RefreshCw size={18} />
            Aggiorna Risultati
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <SidebarLayout sidebar={sidebar} sidebarPosition="right">
      {mainContent}
    </SidebarLayout>
  );
};

export default SurveyResults;
