import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Vote, RefreshCw, TrendingUp, Users, Award } from 'lucide-react';
import { SurveyResultsResponse } from '../types';
import { surveyApi } from '../services/api';

const SurveyResults: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [results, setResults] = useState<SurveyResultsResponse | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    if (id) {
      loadResults(parseInt(id));
    }
  }, [id]);

  const loadResults = async (surveyId: number) => {
    try {
      setLoading(true);
      const resultsData = await surveyApi.getSurveyResults(surveyId);
      setResults(resultsData);
      setError(null);
    } catch (err) {
      setError('Errore nel caricamento dei risultati');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const getPercentage = (voteCount: number, total: number): number => {
    if (total === 0) return 0;
    return Math.round((voteCount / total) * 100);
  };

  if (loading) {
    return <div className="loading">Caricamento risultati...</div>;
  }

  if (error || !results) {
    return (
      <div className="error">
        {error || 'Risultati non trovati'}
        <button onClick={() => navigate('/')} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Torna alla Lista
        </button>
      </div>
    );
  }

  const topResult = results?.results.length > 0 
    ? results.results.reduce((prev, current) => 
        (current.vote_count > prev.vote_count) ? current : prev
      )
    : null;

  return (
    <div>
      <div style={{ 
        marginBottom: '2rem', 
        display: 'flex', 
        gap: '1rem',
        flexWrap: 'wrap'
      }}>
        <button onClick={() => navigate('/')} className="btn btn-secondary">
          <ArrowLeft size={18} />
          Torna alla Lista
        </button>
        <button onClick={() => navigate(`/survey/${results.survey_id}`)} className="btn btn-primary">
          <Vote size={18} />
          Partecipa al Sondaggio
        </button>
      </div>

      <div className="card">
        <div style={{ marginBottom: '2rem' }}>
          <h1 className="card-title">Risultati del Sondaggio</h1>
          <p className="card-description">{results.survey_title}</p>
        </div>

        {/* Stats cards */}
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
          gap: '1rem',
          marginBottom: '2.5rem'
        }}>
          <div style={{
            padding: '1.5rem',
            borderRadius: '16px',
            background: 'linear-gradient(135deg, rgba(102, 126, 234, 0.1) 0%, rgba(118, 75, 162, 0.1) 100%)',
            border: '2px solid rgba(102, 126, 234, 0.2)',
            display: 'flex',
            flexDirection: 'column',
            gap: '0.5rem'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', color: '#667eea' }}>
              <Users size={20} />
              <span style={{ fontSize: '0.875rem', fontWeight: '600' }}>Totale Voti</span>
            </div>
            <div style={{ fontSize: '2rem', fontWeight: '800', color: '#0f172a' }}>
              {results.total_votes}
            </div>
          </div>

          <div style={{
            padding: '1.5rem',
            borderRadius: '16px',
            background: 'linear-gradient(135deg, rgba(79, 172, 254, 0.1) 0%, rgba(0, 242, 254, 0.1) 100%)',
            border: '2px solid rgba(79, 172, 254, 0.2)',
            display: 'flex',
            flexDirection: 'column',
            gap: '0.5rem'
          }}>
            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', color: '#4facfe' }}>
              <TrendingUp size={20} />
              <span style={{ fontSize: '0.875rem', fontWeight: '600' }}>Opzioni</span>
            </div>
            <div style={{ fontSize: '2rem', fontWeight: '800', color: '#0f172a' }}>
              {results.results.length}
            </div>
          </div>

          {topResult && results.total_votes > 0 && (
            <div style={{
              padding: '1.5rem',
              borderRadius: '16px',
              background: 'linear-gradient(135deg, rgba(250, 112, 154, 0.1) 0%, rgba(254, 225, 64, 0.1) 100%)',
              border: '2px solid rgba(250, 112, 154, 0.2)',
              display: 'flex',
              flexDirection: 'column',
              gap: '0.5rem',
              gridColumn: results.results.length === 2 ? '1 / -1' : 'auto'
            }}>
              <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', color: '#fa709a' }}>
                <Award size={20} />
                <span style={{ fontSize: '0.875rem', fontWeight: '600' }}>Pi√π Votata</span>
              </div>
              <div style={{ 
                fontSize: '1.125rem', 
                fontWeight: '700', 
                color: '#0f172a',
                lineHeight: 1.3
              }}>
                {topResult.option_text}
              </div>
              <div style={{ fontSize: '0.875rem', color: '#64748b' }}>
                {topResult.vote_count} voti ({getPercentage(topResult.vote_count, results.total_votes)}%)
              </div>
            </div>
          )}
        </div>

        {results.total_votes === 0 ? (
          <div className="text-center" style={{ padding: '4rem 2rem' }}>
            <Users size={64} style={{ 
              margin: '0 auto 2rem', 
              color: '#667eea',
              opacity: 0.5 
            }} />
            <h3 style={{ fontSize: '1.5rem', marginBottom: '1rem' }}>
              Nessun voto ancora registrato
            </h3>
            <p style={{ color: '#64748b', marginBottom: '2rem', fontSize: '1.125rem' }}>
              Sii il primo a votare e a far sentire la tua voce!
            </p>
            <button 
              onClick={() => navigate(`/survey/${results.survey_id}`)}
              className="btn btn-primary"
            >
              <Vote size={18} />
              Vota Ora
            </button>
          </div>
        ) : (
          <div>
            <h3 style={{ 
              marginBottom: '1.5rem',
              fontSize: '1.25rem',
              fontWeight: '700',
              color: '#0f172a',
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem'
            }}>
              <TrendingUp size={22} style={{ color: '#667eea' }} />
              Distribuzione dei Voti:
            </h3>

            {results.results
              .sort((a, b) => b.vote_count - a.vote_count)
              .map((result, index) => {
                const percentage = getPercentage(result.vote_count, results.total_votes);
                const isTopResult = result.option_id === topResult?.option_id;

                return (
                  <div 
                    key={result.option_id} 
                    className="result-item"
                    style={{
                      animationDelay: `${index * 0.1}s`,
                      position: 'relative'
                    }}
                  >
                    {isTopResult && (
                      <div style={{
                        position: 'absolute',
                        top: '-8px',
                        right: '1rem',
                        background: 'linear-gradient(135deg, #fa709a 0%, #fee140 100%)',
                        color: 'white',
                        padding: '0.25rem 0.75rem',
                        borderRadius: '12px',
                        fontSize: '0.75rem',
                        fontWeight: '700',
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.25rem',
                        boxShadow: '0 4px 12px rgba(250, 112, 154, 0.4)'
                      }}>
                        <Award size={14} />
                        Prima
                      </div>
                    )}

                    <div className="result-option">
                      <span style={{ fontSize: '1.125rem' }}>
                        <strong>{result.option_text}</strong>
                      </span>
                      <span style={{ 
                        fontSize: '1rem',
                        color: '#667eea',
                        fontWeight: '700'
                      }}>
                        {result.vote_count} {result.vote_count === 1 ? 'voto' : 'voti'}
                      </span>
                    </div>

                    <div className="result-bar">
                      <div 
                        className="result-bar-fill" 
                        style={{ width: `${percentage}%` }}
                      />
                      <div className="result-percentage">
                        {percentage}%
                      </div>
                    </div>
                  </div>
                );
              })}
          </div>
        )}

        <div style={{ 
          marginTop: '2.5rem', 
          paddingTop: '2rem',
          borderTop: '2px solid rgba(148, 163, 184, 0.1)',
          display: 'flex',
          justifyContent: 'center'
        }}>
          <button 
            onClick={() => loadResults(results.survey_id)}
            className="btn btn-secondary"
          >
            <RefreshCw size={18} />
            Aggiorna Risultati
          </button>
        </div>
      </div>
    </div>
  );
};

export default SurveyResults;
