import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { ArrowLeft, Vote, RefreshCw, TrendingUp, Users, Award, MessageSquare, Calendar, User, Star, Heart, ChevronDown, ChevronUp } from 'lucide-react';
import { SurveyResultsResponse, QuestionType } from '../types';
import { surveyApi } from '../services/api';
import SidebarLayout from '../components/SidebarLayout';
import LikeRating from '../components/LikeRating';

const SurveyResults: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();
  const [results, setResults] = useState<SurveyResultsResponse | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [expandedOptions, setExpandedOptions] = useState<Set<number>>(new Set());

  useEffect(() => {
    if (id) {
      loadResults(parseInt(id));
    }
  }, [id]);

  const loadResults = async (surveyId: number) => {
    try {
      setLoading(true);
      const resultsData = await surveyApi.getSurveyResults(surveyId);
      setResults(resultsData);
      setError(null);
    } catch (err) {
      setError('Errore nel caricamento dei risultati');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const toggleOption = (optionId: number) => {
    setExpandedOptions(prev => {
      const newSet = new Set(prev);
      if (newSet.has(optionId)) {
        newSet.delete(optionId);
      } else {
        newSet.add(optionId);
      }
      return newSet;
    });
  };

  const getPercentage = (voteCount: number, total: number): number => {
    if (total === 0) return 0;
    return Math.round((voteCount / total) * 100);
  };

  const renderRatingValue = (value: number, icon: string = 'number'): React.ReactNode => {
    const count = Math.floor(value);

    if (icon === 'star') {
      return (
        <span style={{ display: 'flex', alignItems: 'center', gap: '2px' }}>
          {Array.from({ length: count }).map((_, i) => (
            <Star key={i} size={18} fill="#fbbf24" color="#fbbf24" />
          ))}
        </span>
      );
    } else if (icon === 'heart') {
      return (
        <span style={{ display: 'flex', alignItems: 'center', gap: '2px' }}>
          {Array.from({ length: count }).map((_, i) => (
            <Heart key={i} size={18} fill="#ec4899" color="#ec4899" />
          ))}
        </span>
      );
    } else {
      return <span>{value}</span>;
    }
  };

  if (loading) {
    return <div className="loading">Caricamento risultati...</div>;
  }

  if (error || !results) {
    return (
      <div className="error">
        {error || 'Risultati non trovati'}
        <button onClick={() => navigate('/surveys')} className="btn btn-primary" style={{ marginLeft: '1rem' }}>
          Torna alla Lista
        </button>
      </div>
    );
  }

  const topResult = results.results && results.results.length > 0 
    ? results.results.reduce((prev, current) => 
        (current.vote_count > prev.vote_count) ? current : prev
      )
    : null;

  const isChoiceType = results.question_type === QuestionType.SINGLE_CHOICE || 
                       results.question_type === QuestionType.MULTIPLE_CHOICE;
  const isNumericType = results.question_type === QuestionType.RATING || 
                        results.question_type === QuestionType.SCALE;
  const isDateType = results.question_type === QuestionType.DATE;

  // Pannello laterale con solo commenti
  const sidebar = (
    <div>
      {/* Sezione Commenti */}
      <div style={{
        display: 'flex',
        alignItems: 'center',
        gap: '0.5rem',
        marginBottom: '1.5rem',
        paddingBottom: '1rem',
        borderBottom: '2px solid #e2e8f0'
      }}>
        <MessageSquare size={20} style={{ color: '#6366f1' }} />
        <h3 style={{ 
          margin: 0, 
          fontSize: '1.125rem', 
          fontWeight: '600', 
          color: '#0f172a',
          flex: 1
        }}>
          Commenti
        </h3>
        {results && results.open_responses && results.open_responses.filter(r => !r.option_id).length > 0 && (
          <span style={{
            background: '#6366f1',
            color: 'white',
            borderRadius: '50%',
            width: '24px',
            height: '24px',
            fontSize: '0.75rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontWeight: '600'
          }}>
            {results.open_responses.filter(r => !r.option_id).length}
          </span>
        )}
      </div>

      {/* Lista Commenti come Post */}
      {results && results.open_responses && results.open_responses.filter(r => !r.option_id).length > 0 ? (
        <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
          {results.open_responses.filter(r => !r.option_id).map((response, index) => (
            <div 
              key={response.id} 
              style={{
                background: '#f8fafc',
                borderRadius: '8px',
                padding: '1rem',
                border: '1px solid #e2e8f0',
                transition: 'all 0.2s ease'
              }}
            >
              {/* Header del post */}
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.75rem',
                marginBottom: '0.75rem'
              }}>
                {/* Avatar con iniziale */}
                <div style={{
                  width: '36px',
                  height: '36px',
                  borderRadius: '50%',
                  background: `hsl(${(index * 137.5) % 360}, 65%, 55%)`,
                  color: 'white',
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'center',
                  fontWeight: '600',
                  fontSize: '0.875rem',
                  flexShrink: 0
                }}>
                  <User size={18} />
                </div>

                <div style={{ flex: 1, minWidth: 0 }}>
                  <div style={{ 
                    fontSize: '0.875rem', 
                    fontWeight: '600',
                    color: '#0f172a',
                    marginBottom: '0.125rem'
                  }}>
                    Utente #{index + 1}
                  </div>
                  <div style={{
                    fontSize: '0.75rem',
                    color: '#64748b',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.25rem'
                  }}>
                    <Calendar size={12} />
                    {new Date(response.responded_at).toLocaleDateString('it-IT', {
                      day: 'numeric',
                      month: 'short',
                      year: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </div>
                </div>
              </div>

              {/* Contenuto del commento */}
              <div style={{
                fontSize: '0.875rem',
                color: '#475569',
                lineHeight: 1.6,
                whiteSpace: 'pre-wrap',
                wordBreak: 'break-word'
              }}>
                {response.response_text}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div style={{ 
          textAlign: 'center', 
          padding: '2rem 1rem',
          color: '#94a3b8'
        }}>
          <MessageSquare size={32} style={{ 
            margin: '0 auto 0.75rem',
            opacity: 0.5
          }} />
          <p style={{ 
            fontSize: '0.875rem',
            margin: 0
          }}>
            Nessun commento ancora
          </p>
        </div>
      )}
    </div>
  );

  const mainContent = (
    <div>
      <div style={{ 
        marginBottom: '2rem', 
        display: 'flex', 
        gap: '1rem',
        flexWrap: 'wrap'
      }}>
        <button onClick={() => navigate('/surveys')} className="btn btn-secondary">
          <ArrowLeft size={18} />
          Torna alla Lista
        </button>
        <button onClick={() => navigate(`/survey/${results.survey_id}`)} className="btn btn-primary">
          <Vote size={18} />
          Partecipa al Sondaggio
        </button>
      </div>

      <div className="card">
        <div style={{ marginBottom: '1.5rem' }}>
          {/* Titolo e Badges sulla stessa riga */}
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'flex-start',
            marginBottom: '0.5rem',
            gap: '1rem'
          }}>
            <h1 className="card-title" style={{ marginBottom: 0 }}>Risultati del Sondaggio</h1>

            {/* Badges: Gradimento e Tipologia in alto a destra */}
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '0.5rem',
              flexShrink: 0
            }}>
              {/* Gradimento */}
              {results.like_stats && results.like_stats.total_likes > 0 && (
                <div style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.375rem',
                  fontSize: '0.875rem'
                }}>
                  <LikeRating
