                        onMouseLeave={(e) => {
                          if (optionResponses.length > 0) {
                            e.currentTarget.style.background = '#f8fafc';
                            e.currentTarget.style.borderColor = '#e2e8f0';
                          }
                        }}
                      >
                        <div>
                          <div style={{
                            fontSize: '1rem',
                            fontWeight: '600',
                            color: '#0f172a',
                            marginBottom: '0.25rem'
                          }}>
                            {option.option_text}
                          </div>
                          <div style={{
                            fontSize: '0.875rem',
                            color: '#64748b'
                          }}>
                            {optionResponses.length} {optionResponses.length === 1 ? 'risposta' : 'risposte'}
                          </div>
                        </div>
                        {optionResponses.length > 0 && (
                          <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            color: '#6366f1',
                            transition: 'transform 0.2s ease'
                          }}>
                            {isExpanded ? <ChevronUp size={20} /> : <ChevronDown size={20} />}
                          </div>
                        )}
                      </div>

                      {isExpanded && optionResponses.length > 0 && (
                        <div style={{ 
                          display: 'flex', 
                          flexDirection: 'column', 
                          gap: '0.75rem', 
                          paddingLeft: '1rem',
                          animation: 'fadeIn 0.2s ease'
                        }}>
                          {optionResponses.map((response, index) => (
                            <div
                              key={response.id}
                              style={{
                                background: '#ffffff',
                                borderRadius: '6px',
                                padding: '0.875rem',
                                border: '1px solid #e2e8f0',
                                fontSize: '0.875rem',
                                color: '#475569',
                                lineHeight: 1.6
                              }}
                            >
                              <div style={{
                                display: 'flex',
                                alignItems: 'center',
                                gap: '0.5rem',
                                marginBottom: '0.5rem',
                                fontSize: '0.75rem',
                                color: '#94a3b8'
                              }}>
                                <User size={14} />
                                <span>Utente #{index + 1}</span>
                              </div>
                              <div style={{ whiteSpace: 'pre-wrap', wordBreak: 'break-word' }}>
                                {response.response_text}
                              </div>
                            </div>
                          ))}
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        )}

        <div style={{
          marginTop: '2rem',
          paddingTop: '1.5rem',
          borderTop: '1px solid #e2e8f0',
          display: 'flex',
          justifyContent: 'center'
        }}>
          <button
            onClick={() => loadResults(results.survey_id)}
            className="btn btn-secondary"
          >
            <RefreshCw size={18} />
            Aggiorna Risultati
          </button>
        </div>
      </div>
    </div>
  );

  return (
    <SidebarLayout sidebar={sidebar} sidebarPosition="right">
      {mainContent}
    </SidebarLayout>
  );
};

export default SurveyResults;
