import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FileText, MessageSquare, PlusCircle, X, Save, ArrowLeft, Calendar, Settings, Star, Heart, Hash } from 'lucide-react';
import { surveyApi } from '../services/api';
import { SurveyCreate, QuestionType } from '../types';
import TagManager from '../components/TagManager';

const CreateSurvey: React.FC = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState<SurveyCreate>({
    title: '',
    description: '',
    question_type: QuestionType.SINGLE_CHOICE,
    options: ['', ''],
    tag_ids: [],
    allow_multiple_responses: false,
    allow_custom_options: false,
    require_comment: false,
    rating_icon: 'star',
    min_value: 1,
    max_value: 5
  });
  const [expiryDate, setExpiryDate] = useState<string>('');
  const [expiryTime, setExpiryTime] = useState<string>('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    // Validazione
    if (!formData.title.trim()) {
      setError('Il titolo √® obbligatorio');
      return;
    }

    // Validazione opzioni per sondaggi a scelta
    if ([QuestionType.SINGLE_CHOICE, QuestionType.MULTIPLE_CHOICE].includes(formData.question_type)) {
      const validOptions = formData.options.filter(opt => opt.trim());
      if (validOptions.length < 2) {
        setError('Devi inserire almeno 2 opzioni');
        return;
      }
    }

    try {
      setLoading(true);
      setError(null);

      const surveyToCreate: SurveyCreate = {
        ...formData,
        options: formData.options.filter(opt => opt.trim())
      };

      // Rimuovi opzioni non necessarie per altri tipi
      if (![QuestionType.SINGLE_CHOICE, QuestionType.MULTIPLE_CHOICE].includes(formData.question_type)) {
        surveyToCreate.options = [];
      }

      // Combina data e orario se entrambi impostati
      if (expiryDate && expiryTime) {
        // Crea un oggetto Date locale e convertilo in ISO string
        const localDateTime = new Date(`${expiryDate}T${expiryTime}:00`);
        surveyToCreate.expires_at = localDateTime.toISOString();
      } else if (expiryDate) {
        // Se solo data, imposta a fine giornata
        const localDateTime = new Date(`${expiryDate}T23:59:00`);
        surveyToCreate.expires_at = localDateTime.toISOString();
      }

      const newSurvey = await surveyApi.createSurvey(surveyToCreate);
      navigate(`/survey/${newSurvey.id}`);
    } catch (err) {
      setError('Errore nella creazione del sondaggio');
      console.error('Errore:', err);
    } finally {
      setLoading(false);
    }
  };

  const addOption = () => {
    setFormData(prev => ({
      ...prev,
      options: [...prev.options, '']
    }));
  };

  const removeOption = (index: number) => {
    if (formData.options.length > 2) {
      setFormData(prev => ({
        ...prev,
        options: prev.options.filter((_, i) => i !== index)
      }));
    }
  };

  const updateOption = (index: number, value: string) => {
    setFormData(prev => ({
      ...prev,
      options: prev.options.map((opt, i) => i === index ? value : opt)
    }));
  };

  const questionTypeOptions = [
    { value: QuestionType.SINGLE_CHOICE, label: 'Scelta Singola', icon: '‚ö™' },
    { value: QuestionType.MULTIPLE_CHOICE, label: 'Scelta Multipla', icon: '‚òëÔ∏è' },
    { value: QuestionType.RATING, label: 'Valutazione (Stelle/Cuori)', icon: '‚≠ê' },
    { value: QuestionType.SCALE, label: 'Scala Numerica (1-N)', icon: 'üìä' },
    { value: QuestionType.OPEN_TEXT, label: 'Risposta Aperta', icon: '‚úçÔ∏è' },
    { value: QuestionType.NUMBER, label: 'Numero', icon: '#Ô∏è‚É£' },
    { value: QuestionType.DATE, label: 'Data', icon: 'üìÖ' },
  ];

  const showOptions = [QuestionType.SINGLE_CHOICE, QuestionType.MULTIPLE_CHOICE].includes(formData.question_type);
  const showRatingOptions = formData.question_type === QuestionType.RATING;
  const showScaleOptions = formData.question_type === QuestionType.SCALE;

  return (
    <div>
      <div style={{ marginBottom: '2rem' }}>
        <h1 style={{ 
          fontSize: '1.875rem', 
          fontWeight: '600', 
          color: '#0f172a',
          marginBottom: '0.375rem'
        }}>
          Crea Nuovo Sondaggio
        </h1>
        <p style={{ 
          color: '#64748b', 
          fontSize: '0.9375rem',
          fontWeight: '400'
        }}>
          Configura il tuo sondaggio con opzioni avanzate
        </p>
      </div>

      {error && <div className="error">{error}</div>}

      <div className="card">
        <form onSubmit={handleSubmit}>
          {/* Titolo */}
          <div className="form-group">
            <label className="form-label">
              <FileText size={20} />
              Titolo del Sondaggio *
            </label>
            <input
              type="text"
              className="form-input"
              value={formData.title}
              onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
              placeholder="Es: Qual √® la tua opinione sul servizio?"
              required
            />
          </div>

          {/* Descrizione */}
          <div className="form-group">
            <label className="form-label">
              <MessageSquare size={20} />
              Descrizione (opzionale)
            </label>
            <textarea
              className="form-textarea"
              value={formData.description}
              onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
              placeholder="Aggiungi una descrizione per fornire pi√π contesto..."
            />
          </div>

          {/* Tipo Domanda */}
          <div className="form-group">
            <label className="form-label">
              <Settings size={20} />
              Tipo di Domanda *
            </label>
            <select
              className="form-input"
              value={formData.question_type}
              onChange={(e) => setFormData(prev => ({ 
                ...prev, 
                question_type: e.target.value as QuestionType 
              }))}
            >
              {questionTypeOptions.map(opt => (
                <option key={opt.value} value={opt.value}>
                  {opt.icon} {opt.label}
                </option>
              ))}
            </select>
          </div>

          {/* Opzioni Rating */}
          {showRatingOptions && (
            <div className="form-group">
              <label className="form-label">
                <Star size={20} />
                Icona Rating
              </label>
              <div style={{ display: 'flex', gap: '0.75rem' }}>
                {[
                  { value: 'star', label: 'Stelle', icon: <Star size={24} /> },
                  { value: 'heart', label: 'Cuori', icon: <Heart size={24} /> },
                  { value: 'number', label: 'Numeri', icon: <Hash size={24} /> }
                ].map(option => (
                  <button
                    key={option.value}
                    type="button"
                    onClick={() => setFormData(prev => ({ ...prev, rating_icon: option.value as any }))}
                    style={{
                      padding: '0.75rem 1.25rem',
                      borderRadius: '8px',
                      border: `2px solid ${formData.rating_icon === option.value ? '#6366f1' : '#e2e8f0'}`,
                      background: formData.rating_icon === option.value ? '#6366f1' : 'white',
                      color: formData.rating_icon === option.value ? 'white' : '#475569',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      gap: '0.5rem',
                      fontWeight: '500',
                      transition: 'all 0.2s'
                    }}
                  >
                    {option.icon}
                    {option.label}
                  </button>
                ))}
              </div>
            </div>
          )}

          {/* Scala Min/Max */}
          {(showScaleOptions || showRatingOptions) && (
            <div className="form-group">
              <label className="form-label">
                <Settings size={20} />
                Intervallo {showRatingOptions ? 'Rating' : 'Scala'}
              </label>
              <div style={{ display: 'flex', gap: '1rem' }}>
                <div style={{ flex: 1 }}>
                  <label style={{ fontSize: '0.875rem', color: '#64748b', display: 'block', marginBottom: '0.375rem' }}>
                    Min
                  </label>
                  <input
                    type="number"
                    className="form-input"
                    value={formData.min_value}
                    onChange={(e) => setFormData(prev => ({ ...prev, min_value: parseInt(e.target.value) || 1 }))}
                    min={1}
                    max={formData.max_value ? formData.max_value - 1 : 10}
                    style={{ margin: 0 }}
                  />
                </div>
                <div style={{ flex: 1 }}>
                  <label style={{ fontSize: '0.875rem', color: '#64748b', display: 'block', marginBottom: '0.375rem' }}>
                    Max
                  </label>
                  <input
                    type="number"
                    className="form-input"
                    value={formData.max_value}
                    onChange={(e) => setFormData(prev => ({ ...prev, max_value: parseInt(e.target.value) || 5 }))}
                    min={formData.min_value ? formData.min_value + 1 : 2}
                    max={10}
                    style={{ margin: 0 }}
                  />
                </div>
              </div>
            </div>
          )}

          {/* Label scala */}
          {showScaleOptions && (
            <div className="form-group">
              <label className="form-label">
                Etichette Scala (opzionale)
              </label>
              <div style={{ display: 'flex', gap: '1rem' }}>
                <input
                  type="text"
                  className="form-input"
                  value={formData.scale_min_label || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, scale_min_label: e.target.value }))}
                  placeholder="Es: Molto insoddisfatto"
                  style={{ flex: 1, margin: 0 }}
                />
                <input
                  type="text"
                  className="form-input"
                  value={formData.scale_max_label || ''}
                  onChange={(e) => setFormData(prev => ({ ...prev, scale_max_label: e.target.value }))}
                  placeholder="Es: Molto soddisfatto"
                  style={{ flex: 1, margin: 0 }}
                />
              </div>
            </div>
          )}

          {/* Opzioni di Risposta */}
          {showOptions && (
            <div className="form-group">
              <label className="form-label">
                <PlusCircle size={20} />
                Opzioni di Risposta * (minimo 2)
              </label>
              {formData.options.map((option, index) => (
                <div key={index} style={{ 
                  display: 'flex', 
                  marginBottom: '1rem', 
                  alignItems: 'center',
                  gap: '0.75rem'
                }}>
                  <span style={{ 
                    minWidth: '28px',
                    height: '28px',
                    borderRadius: '6px',
                    background: '#6366f1',
                    color: 'white',
                    display: 'flex',
                    alignItems: 'center',
                    justifyContent: 'center',
                    fontWeight: '500',
                    fontSize: '0.8125rem'
                  }}>
                    {index + 1}
                  </span>
                  <input
                    type="text"
                    className="form-input"
                    value={option}
                    onChange={(e) => updateOption(index, e.target.value)}
                    placeholder={`Opzione ${index + 1}`}
                    style={{ flex: 1, margin: 0 }}
                  />
                  {formData.options.length > 2 && (
                    <button
                      type="button"
                      onClick={() => removeOption(index)}
                      className="btn btn-secondary"
                      style={{ 
                        padding: '0.625rem',
                        minWidth: 'auto',
                        aspectRatio: '1/1'
                      }}
                      title="Rimuovi opzione"
                    >
                      <X size={18} />
                    </button>
                  )}
                </div>
              ))}

              <button
                type="button"
                onClick={addOption}
                className="btn btn-secondary"
                style={{ marginTop: '0.75rem', width: '100%' }}
              >
                <PlusCircle size={18} />
                Aggiungi Opzione
              </button>
            </div>
          )}

          {/* Scadenza */}
          <div className="form-group">
            <label className="form-label">
              <Calendar size={20} />
              Data di Scadenza (opzionale)
            </label>
            <div style={{ display: 'flex', gap: '1rem' }}>
              <div style={{ flex: 1 }}>
                <label style={{ 
                  display: 'block', 
                  fontSize: '0.8125rem', 
                  color: '#64748b', 
                  marginBottom: '0.5rem',
                  fontWeight: '500'
                }}>
                  Data
                </label>
                <input
                  type="date"
                  className="form-input"
                  value={expiryDate}
                  onChange={(e) => setExpiryDate(e.target.value)}
                  style={{ margin: 0 }}
                />
              </div>
              <div style={{ flex: 1 }}>
                <label style={{ 
                  display: 'block', 
                  fontSize: '0.8125rem', 
                  color: '#64748b', 
                  marginBottom: '0.5rem',
                  fontWeight: '500'
                }}>
                  Orario
                </label>
                <input
                  type="time"
                  className="form-input"
                  value={expiryTime}
                  onChange={(e) => setExpiryTime(e.target.value)}
                  style={{ margin: 0 }}
                />
              </div>
            </div>
            <p style={{ fontSize: '0.8125rem', color: '#64748b', marginTop: '0.5rem' }}>
              Se impostata, il sondaggio non accetter√† pi√π voti dopo questa data e ora
            </p>
          </div>

          {/* Tag */}
          <div className="form-group">
            <label className="form-label">
              üè∑Ô∏è Tag e Categorie (opzionale)
            </label>
            <TagManager
              selectedTagIds={formData.tag_ids || []}
              onChange={(tagIds) => setFormData(prev => ({ ...prev, tag_ids: tagIds }))}
            />
          </div>

          {/* Opzioni Avanzate */}
          <div className="form-group">
            <label className="form-label">
              <Settings size={20} />
              Opzioni Avanzate
            </label>
            <div style={{ 
              display: 'flex', 
              flexDirection: 'column', 
              gap: '0.75rem',
              padding: '1rem',
              background: '#f8fafc',
              borderRadius: '8px',
              border: '1px solid #e2e8f0'
            }}>
              {[QuestionType.SINGLE_CHOICE, QuestionType.MULTIPLE_CHOICE].includes(formData.question_type) && (
                <label style={{ 
                  display: 'flex', 
                  alignItems: 'center', 
                  gap: '0.75rem',
                  cursor: 'pointer',
                  fontSize: '0.9375rem'
                }}>
                  <input
                    type="checkbox"
                    checked={formData.allow_custom_options || false}
                    onChange={(e) => setFormData(prev => ({ ...prev, allow_custom_options: e.target.checked }))}
                    style={{ width: '18px', height: '18px', cursor: 'pointer' }}
                  />
                  <span>
                    <strong>Permetti opzioni personalizzate</strong>
                    <div style={{ fontSize: '0.8125rem', color: '#64748b' }}>
                      Gli utenti possono aggiungere nuove opzioni oltre a quelle esistenti
                    </div>
                  </span>
                </label>
              )}

              <label style={{ 
                display: 'flex', 
                alignItems: 'center', 
                gap: '0.75rem',
                cursor: 'pointer',
                fontSize: '0.9375rem'
              }}>
                <input
                  type="checkbox"
                  checked={formData.require_comment || false}
                  onChange={(e) => setFormData(prev => ({ ...prev, require_comment: e.target.checked }))}
                  style={{ width: '18px', height: '18px', cursor: 'pointer' }}
                />
                <span>
                  <strong>Richiedi commento</strong>
                  <div style={{ fontSize: '0.8125rem', color: '#64748b' }}>
                    Gli utenti devono lasciare un commento quando votano
                  </div>
                </span>
              </label>
            </div>
          </div>

          {/* Pulsanti */}
          <div style={{ 
            display: 'flex', 
            gap: '0.5rem', 
            marginTop: '2rem',
            paddingTop: '1.5rem',
            borderTop: '1px solid #e2e8f0'
          }}>
            <button
              type="submit"
              className="btn btn-primary"
              disabled={loading}
              style={{ flex: 1 }}
            >
              <Save size={18} />
              {loading ? 'Creazione...' : 'Crea Sondaggio'}
            </button>

            <button
              type="button"
              onClick={() => navigate('/surveys')}
              className="btn btn-secondary"
            >
              <ArrowLeft size={18} />
              Annulla
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateSurvey;
